#!/bin/bash

p_list=(PS003A PS012A PS014A PS017A PS019A PS021A PS025A PS026A PS027A PS032A PS033A PS035A PS037A PS038A PS041A PS044A PS045A PS046A PS052A PS053A PS057A PS002A PS004A PS006A PS007A PS011A PS016A PS018A PS020A PS022A PS024A PS028A PS029A PS031A PS034A PS036A PS039A PS043A PS047A PS048A PS049A PS054A PS056A PS058A PS059A PS060A); for i in ${p_list[@]}; do CSFvol=$(echo `fslstats /project/3011154.01/MJ/FC/${i}/rs/nui_hp/*pve_0* -M` `fslstats /project/3011154.01/MJ/FC/${i}/rs/nui_hp/*pve_0* -V` | awk '{ CSF = $1 * $3; print CSF }'); GMvol=$(echo -n `fslstats /project/3011154.01/MJ/FC/${i}/rs/nui_hp/*pve_1* -M` `fslstats /project/3011154.01/MJ/FC/${i}/rs/nui_hp/*pve_1* -V` | awk '{ GM = $1 * $3; print GM }'); WMvol=$(echo -n `fslstats /project/3011154.01/MJ/FC/${i}/rs/nui_hp/*pve_2* -M` `fslstats /project/3011154.01/MJ/FC/${i}/rs/nui_hp/*pve_2* -V` | awk '{ WM = $1 * $3; print WM }'); TICV=$(echo $CSFvol $GMvol $WMvol | awk '{ TICV = $1 + $2 + $3; print TICV }'); echo $TICV >> /project/3011154.01/MJ/ParkInShapeTICV.txt; done
